package javapratices;

class market
{
	void reverse()
	{
		System.out.println("Reverse Number : ");
		int i=10;
		do
		{
			System.out.println(i);
			i--;
		}
		while(i>=1);

	}
}
public class reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		market Ass = new market();
		Ass.reverse();
	}

}
